import React, { useState, useCallback, useMemo} from 'react';
import { Form, Input, Button } from 'antd';
import PropTypes from 'prop-types';
import Link from 'next/link';
import styled from 'styled-components';

// const ButtonWrapper = styled.div`
//     margin-top: 10px;
//  `;

//  const FormWrapper = styled(Form)`
//     padding: 10px;
// `;

const LoginForm = ({setIsLoggedIn}) => {
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');

    const onChangeId = useCallback((e) => {
            setId(e.target.value); 
        }, []);

    const onChangePassword = useCallback((e) => {
            setPassword(e.target.value); 
        }, []);

    const onsubmitForm = useCallback(() => {
        console.log(id, password);
        setIsLoggedIn(true);
    },[id, password]);

     const style = useMemo(() => ({marginTop:10}),[]);

     const style2 = useMemo(() => ({padding:10}),[]);

    return(
        <Form style={style2} onFinish={onsubmitForm}>
            <div>
                <label htmlFor="user-id">아이디</label>
                <br />
                <Input name="user-id" value={id} onChange={onChangeId} required />
            </div>
            <div>
                <label htmlFor="user-password">비밀번호</label>
                <br />
                <Input name="user-password" type="password"
                    value={password} onChange={onChangePassword} required />
            </div>
            <Button style={style}>
                <Button style={style} type="primary" htmlType="submit" loading={false}>로그인</Button>
                <Link href="/singup"><a><Button>회원가입</Button></a></Link>
            </Button>
        </Form>
    );
};

LoginForm.propTypes = {
    setIsLoggedIn: PropTypes.node.isRequired,
};
export default LoginForm
